const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#0F1923",
  },
  header: {
    backgroundColor: "#1E2A38",
    height: 80,
    justifyContent: "center",
    alignItems: "center",
    borderBottomWidth: 1,
    borderBottomColor: "#36454F",
  },
  headerText: {
    color: "#FFFFFF",
    fontSize: 20,
    fontWeight: "bold",
  },
  questionContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },
  questionText: {
    fontSize: 24,
    fontWeight: "bold",
    textAlign: "center",
    color: "#FFFFFF",
  },
  optionsContainer: {
    flex: 3,
    justifyContent: "flex-start",
    alignItems: "stretch",
    paddingHorizontal: 20,
  },
  option: {
    borderWidth: 1,
    borderColor: "#FFFFFF",
    borderRadius: 10,
    padding: 20,
    marginVertical: 10,
    backgroundColor: "#1E2A38",
  },
  selectedOption: {
    backgroundColor: "#5B75A9",
  },
  optionText: {
    fontSize: 18,
    textAlign: "center",
    color: "#FFFFFF",
  },
  button: {
    marginTop: 30,
    paddingVertical: 10,
    paddingHorizontal: 20,
    backgroundColor: "#f7d08a",
    borderRadius: 5,
    marginLeft: 50,
    marginRight: 50,
  },
  buttonText: {
    color: "white",
    fontSize: 25,
    textAlign: "center",
  },
  scoreText: {
    marginTop: 20,
    fontSize: 16,
    textAlign: "center",
    color: "#f7d08a",
  },
  progressBar: {
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center",
    marginVertical: 20,
  },
  progressBarItem: {
    flex: 1,
    height: 10,
    marginHorizontal: 5,
    backgroundColor: "#FFFFFF",
    borderRadius: 20,
  },
  progressBarItemActive: {
    backgroundColor: "#f7d08a",
  },
  image: {
    width: "100%",
    height: 200,
    borderWidth: 1,
    borderColor: "#FFFFFF",
    borderRadius: 10,
    marginVertical: 20,
  },
});
