import { View, Text, StyleSheet, Image, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import React from "react";

const HomeScreenMain = () => {
  const navigation = useNavigation();
  return (
    <View style={styles.container}>
      <Image
        style={styles.image}
        source={{
          uri: "https://github.com/DKALAJ1239/my-app/blob/master/assets/brand_pic.PNG?raw=true",
        }}
      />

      <View style={styles.descriptionContainer}>
        <Text style={styles.title}>How to Play</Text>
        <View style={styles.descriptionTextContainer}>
          <View style={styles.bulletContainer}>
            <Text style={styles.bullet}>•</Text>
            <Text style={styles.descriptionText}>
              For each correct answer you get 5 points
            </Text>
          </View>
          <View style={styles.bulletContainer}>
            <Text style={styles.bullet}>•</Text>
            <Text style={styles.descriptionText}>
              For each incorrect answer you lose 1 point
            </Text>
          </View>
          <View style={styles.bulletContainer}>
            <Text style={styles.bullet}>•</Text>
            <Text style={styles.descriptionText}>
              You have 10 seconds to answer each question
            </Text>
          </View>
        </View>
        <Pressable
          onPress={() => navigation.navigate("QuizOne")}
          style={styles.startButton}
        >
          <Text style={styles.startButtonText}>Start Quiz</Text>
        </Pressable>
      </View>
    </View>
  );
};

export default HomeScreenMain;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    fontSize: 32,
    fontWeight: "bold",
    color: "#1C2331",
    marginBottom: 20,
  },
  image: {
    height: 300,
    width: "100%",
    resizeMode: "contain",
  },
  descriptionContainer: {
    backgroundColor: "#F7D08A",
    paddingVertical: 40,
    paddingHorizontal: 20,
    marginTop: 20,
    borderRadius: 10,
    width: "90%",
    alignItems: "center",
    justifyContent: "center",
  },
  descriptionTextContainer: {
    marginTop: 20,
    marginBottom: 40,
  },
  bulletContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 5,
  },
  bullet: {
    color: "#1C2331",
    fontSize: 24,
    fontWeight: "bold",
    marginRight: 10,
  },
  descriptionText: {
    color: "#1C2331",
    fontSize: 18,
    textAlign: "left",
    flexShrink: 1,
  },
  startButton: {
    backgroundColor: "#44BD32",
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 10,
  },
  startButtonText: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
});
