import { View, Text, StyleSheet, Image, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import React from "react";

const HomeScreenMain = () => {
  const navigation = useNavigation();
  return (
<View style={styles.container}>
  <Image
    style={styles.image}
    source={{
      uri: "https://github.com/DKALAJ1239/my-app/blob/master/assets/brand_pic.PNG?raw=true",
    }}
  />

  <Text style={styles.title}>Welcome to the Quiz!</Text>

  <View style={styles.descriptionContainer}>
    <Text style={styles.descriptionTitle}>How to Play</Text>
    <Text style={styles.descriptionText}>
      For each correct answer you get 5 points. Answer as many questions as you can within the time limit.
    </Text>
  </View>

  <TouchableOpacity
    onPress={() => navigation.navigate("QuizOne")}
    style={styles.startButton}
  >
    <Text style={styles.startButtonText}>Start Quiz</Text>
  </TouchableOpacity>
</View>

  );
};

export default HomeScreenMain;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    marginTop: 40,
    fontSize: 32,
    fontWeight: "bold",
    color: "#1C2331",
    textAlign: "center",
  },
  image: {
    height: 300,
    width: "100%",
    resizeMode: "contain",
  },
  descriptionContainer: {
    backgroundColor: "#F7D08A",
    paddingVertical: 20,
    paddingHorizontal: 10,
    marginTop: 40,
    borderRadius: 10,
  },
  descriptionTitle: {
    color: "#1C2331",
    fontSize: 22,
    fontWeight: "bold",
    marginBottom: 10,
    textAlign: "center",
  },
  descriptionText: {
    color: "#1C2331",
    fontSize: 16,
    textAlign: "center",
  },
  startButton: {
    backgroundColor: "#44BD32",
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 10,
    marginTop: 40,
  },
  startButtonText: {
    color: "#fff",
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
});
