import { View, Text, StyleSheet, Image, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import React from "react";

const HomeScreenMain = () => {
  const navigation = useNavigation();
  return (
    <View style={{ marginTop: 15 }}>
      <Image
        style={{ height: 370, width: "100%", resizeMode: "contain" }}
        source={{
          uri: "https://github.com/DKALAJ1239/my-app/blob/master/assets/brand_pic.PNG?raw=true",
        }}
      />

      <View style={{ padding: 25 }}>
        <Text
          style={{
            textAlign: "center",
            color: "red",
            fontSize: 30,
            fontWeight: "bold",
          }}
        >
          How to Play
        </Text>
      </View>
      <View
        style={{
          padding: 10,
          backgroundColor: "#F88379",
          borderRadius: 6,
          marginTop: 15,
        }}
      >
        <View
          style={{
            flexDirection: "row",
            alignItems: "center",
            marginVertical: 5,
          }}
        >
          <Text style={{ color: "white" }}>•</Text>
          <Text
            style={{
              marginLeft: 4,
              color: "#722F37",
              fontSize: 15,
              fontWeight: "500",
            }}
          >
            For each correct answer you get 5 points
          </Text>
        </View>
      </View>
      <View
        style={{
          flexDirection: "row",
          alignItems: "center",
          marginVertical: 5,
        }}
      >
        <Text style={{ color: "white" }}>•</Text>
      </View>
      <View>
        <Pressable
          onPress={() => navigation.navigate("QuizOne")}
          style={{
            backgroundColor: "green",
            padding: 15,
            width: 150,
            borderRadius: 15,
            marginLeft: "auto",
            marginRight: "auto",
            marginTop: 30,
          }}
        >
          <Text
            style={{ color: "beige", fontWeight: "600", textAlign: "center" }}
          >
            Start Quiz
          </Text>
        </Pressable>
      </View>
    </View>
  );
};

export default HomeScreenMain;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  title: {
    marginTop: 40,
    fontSize: 32,
    fontWeight: "bold",
    color: "#1C2331",
    textAlign: "center",
  },
  image: {
    height: 300,
    width: "100%",
    resizeMode: "contain",
  },
  descriptionContainer: {
    backgroundColor: "#F7D08A",
    paddingVertical: 20,
    paddingHorizontal: 10,
    marginTop: 40,
    borderRadius: 10,
  },
  descriptionTitle: {
    color: "#1C2331",
    fontSize: 22,
    fontWeight: "bold",
    marginBottom: 10,
    textAlign: "center",
  },
  descriptionText: {
    color: "#1C2331",
    fontSize: 16,
    textAlign: "center",
  },
  startButton: {
    backgroundColor: "#44BD32",
    paddingVertical: 15,
    paddingHorizontal: 30,
    borderRadius: 10,
    marginTop: 40,
  },
  startButtonText: {
    color: "#ff",
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
});
