import { View, Text, StyleSheet, Image, Pressable } from "react-native";
import { useNavigation } from "@react-navigation/native";
import React from "react";

const HomeScreenMain = () => {
  const navigation = useNavigation();
  return (
    <View style={styles.container}>
      <Image
        style={styles.image}
        source={{
          uri: "https://github.com/DKALAJ1239/my-app/blob/master/assets/brand_pic.PNG?raw=true",
        }}
      />

      <View style={styles.descriptionContainer}>
        <Text style={styles.descriptionTitle}>How to Play</Text>
        <View style={styles.descriptionTextContainer}>
          <View style={styles.bulletPointContainer}>
            <Text style={styles.bulletPoint}>â€¢</Text>
            <Text style={styles.descriptionText}>
              For each correct answer you get 5 points
            </Text>
          </View>
        </View>
      </View>

      <Pressable
        onPress={() => navigation.navigate("QuizOne")}
        style={styles.startButton}
      >
        <Text style={styles.startButtonText}>Start Quiz</Text>
      </Pressable>
    </View>
  );
};

export default HomeScreenMain;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },
  image: {
    height: 370,
    width: "100%",
    resizeMode: "contain",
  },
  descriptionContainer: {
    backgroundColor: "#F7D08A",
    paddingVertical: 20,
    paddingHorizontal: 10,
    marginTop: 15,
    borderRadius: 6,
    width: "90%",
    alignItems: "center",
  },
  descriptionTitle: {
    color: "#1C2331",
    fontSize: 30,
    fontWeight: "bold",
    marginBottom: 10,
    textAlign: "center",
  },
  descriptionTextContainer: {
    alignItems: "center",
  },
  bulletPointContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 5,
    width: "90%",
  },
  bulletPoint: {
    color: "white",
    fontSize: 20,
  },
  descriptionText: {
    marginLeft: 10,
    color: "#1C2331",
    fontSize: 20,
    fontWeight: "500",
    flexWrap: "wrap",
    flexShrink: 1,
  },
  startButton: {
    backgroundColor: "green",
    padding: 15,
    width: 150,
    borderRadius: 15,
    marginTop: 30,
  },
  startButtonText: {
    color: "beige",
    fontWeight: "600",
    textAlign: "center",
  },
});
